package assignmentOperators;

public class MultiplyEqualToOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num1 = 8;
		num1 = num1/4;// 4+4 = 8 
		
		num1 = 8;
		num1 /= 4;//Quotient
		
		num1 =7;
		num1 %=4; //Remainder
		
		
	//	num1 += 4;// num1 = num1+4;
		
		System.out.println("num1 = "+num1);
		
		int num2 = 5;
		
		num1 =3;
		
		num1 %= num2;
		
		System.out.println(num1);
		
		
		
		
	}

}
